mapper.select:
select student.*, college.name as collegeName,speciality.name as specialityName,classes.name as className
from student
left join college on student.college_id = college.id
left join speciality on student.speciality_id = speciality.id
left join classes on student.class_id = classes.id

     and id= #{id}
     and username like concat('%', #{username}, '%')
     and password= #{password}
     and name= #{name}
     and avatar= #{avatar}
     and role= #{role}

order by student.id desc

select student.*, college.name as collegeName,speciality.name as specialityName,classes.name as className from student left join college on student.college_id = college.id left join speciality on student.speciality_id = speciality.id left join classes on student.class_id = classes.id
student.class_id =#{classId}
order by student.id desc
